import FlunksGumDrop from 0xFLUNKS_CONTRACT_ADDRESS

transaction(startTime: UFix64, endTime: UFix64, gumPerFlunk: UInt64) {
  prepare(signer: &Account) {
    let admin = signer.storage.borrow<&FlunksGumDrop.Admin>(
      from: FlunksGumDrop.AdminStoragePath
    ) ?? panic("No admin resource found - must be contract owner")

    admin.startDrop(
      startTime: startTime,
      endTime: endTime,
      gumPerFlunk: gumPerFlunk
    )
  }

  execute {
    log("GumDrop event started successfully!")
  }
}
